import React, { Component ,useState } from 'react';

import "./../../assets/css/nucleo-icons.css";
import "./../../assets/css/nucleo-svg.css";
import "./../../assets/css/material-dashboard.css.map";
import "./../../assets/css/material-dashboard.min.css";
import "./../../assets/css/bootstrap.css";

import AddE from '../../components/Add';
 
 
 
import Sidebar from '../../components/Sidebar';
import DashboardNav from '../../components/DashboardNav';
import PopupUser from '../../components/PopupUser';
import { Link } from 'react-router-dom';

// import { Link } from '@material-ui/core';


  
class ExamAdd extends Component {
    render() {
        const [categories, setCategories] = useState([
            { id: 1, name: 'Sharia rulings', subCategories: [] },
            { id: 2, name: 'Category 2', subCategories: ['Subcategory 1', 'Subcategory 2'] },
            { id: 3, name: 'Category 3', subCategories: ['Subcategory 3', 'Subcategory 4'] }
          ]);
          const [selectedCategories, setSelectedCategories] = useState([]);
        
          const handleCategoryChange = (categoryId) => {
            const category = categories.find(cat => cat.id === categoryId);
            if (selectedCategories.includes(category)) {
              setSelectedCategories(prevSelectedCategories =>
                prevSelectedCategories.filter(cat => cat.id !== categoryId)
              );
            } else {
              setSelectedCategories(prevSelectedCategories => [...prevSelectedCategories, category]);
            }
          };
        
          const handleSubcategoryChange = (categoryId, subcategoryIndex, value) => {
            const updatedCategories = [...categories];
            const categoryIndex = updatedCategories.findIndex(cat => cat.id === categoryId);
            updatedCategories[categoryIndex].subCategories[subcategoryIndex] = value;
            setCategories(updatedCategories);
          };
        return (
            <>
                <Sidebar />
                <main className="main-content position-relative max-height-vh-100 h-100 border-radius-lg overflow-x-hidden ">
                <DashboardNav/>
                    <div className="container-fluid py-4">

                        <div className="row">
                 
                       
                            <div className="col-12">

                                <div className="card card-plain">
                                    <div className="card-header add">
                                        <h4 className="font-weight-bolder">اضافة امتحان</h4>
                                        <p className="mb-0">الرجاء اضافة كل المطلوب</p>
                                    </div>
                                    <div className="card-body">
                                        <form role="form">
                                            <div className="row">
 
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >اسم الامتحان :</label>
                                                        <input type="text" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  > المدة الزمنية للامتحان :</label>
                                                        <input type="time" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >تاريخ بداية الامتحان :</label>
                                                        <input type="date" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >تاريخ نهاية الامتحان :</label>
                                                        <input type="date" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >وقت بداية الامتحان :</label>
                                                        <input type="time" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >وقت نهاية الامتحان :</label>
                                                        <input type="time" className="form-control" placeholder="اسم الامتحان" />
                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >شعب الامتحان :</label>
                                                        <PopupUser/>
                                                        <div className="form-check">
                                                            <input className="form-check-input" type="checkbox" value="" id="flexCheckChecked" checked />
                                                            <label className="form-check-label" for="flexCheckChecked">
                                                                كل الشعب
                                                            </label>
                                                        </div>
                                                        <div className="form-check">
                                                            <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                                                            <label className="form-check-label" for="flexCheckDefault">
                                                                أ
                                                            </label>
                                                        </div>
                                                        <div className="form-check">
                                                            <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                                                            <label className="form-check-label" for="flexCheckDefault">
                                                                ب
                                                            </label>
                                                        </div>
                                                        <div className="form-check">
                                                        <td class="align-middle text-center">
                                                            <span class="badge badge-sm bg-gradient-success font-weight-bolder">منذر ابو عبدلله</span>
                                                        </td>
                                                        </div>
                                                      



                                                    </div>
                                                </div>
                                                <div className="col-md-6">
                                                    <div className="form-group  ">
                                                        <label  >التصنيفات :</label>
                                                      
                                                        {categories.map(category => (
          <div key={category.id} className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value={category.id}
              id={`category-${category.id}`}
              checked={selectedCategories.includes(category)}
              onChange={() => handleCategoryChange(category.id)}
            />
            <label className="form-check-label" htmlFor={`category-${category.id}`}>
              {category.name}
            </label>
            {selectedCategories.includes(category) && (
              <div>
                {category.subCategories.map((subCategory, index) => (
                  <div key={index}>
                    <label htmlFor={`subcategory-${category.id}-${index}`}>
                      {subCategory}
                    </label>
                    <input
                      type="text"
                      id={`subcategory-${category.id}-${index}`}
                      value={subCategory}
                      onChange={(e) =>
                        handleSubcategoryChange(category.id, index, e.target.value)
                      }
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}

                                                    </div>
                                                </div>
                                                <div className="col-md-12">
                                                <button type="button" class="btn btn-lg bg-gradient-success btn-lg w-100 mt-4 mb-0 form">اضـــافة</button>

                                                </div>

                                            </div>




                                        </form>
                                    </div>

                                </div>
                            </div>
                        </div>



                    </div>
                </main>



            </>
        )
    }
}
export default ExamAdd;
