import React, { useState } from 'react';

const ExamForm = () => {
    

    const [selectedExam, setSelectedExam] = useState('');
    const [answers, setAnswers] = useState([]);
  
    const examOptions = [
        { value: 'choose', label: 'اختيار من متعدد' },
        { value: 'comp', label: 'اكمل' },
        { value: 'reco', label: 'سجل' },
      // Add more options as needed
    ];
  
    const handleExamChange = (event) => {
      setSelectedExam(event.target.value);
      setAnswers([]); // Reset answers when exam changes
    };
  
    const handleAnswerChange = (index, event) => {
      const newAnswers = [...answers];
      newAnswers[index] = event.target.value;
      setAnswers(newAnswers);
    };
  
    const addAnswer = () => {
      setAnswers([...answers, '']);
    };
  
    const isAddAnswerDisabled = selectedExam !== 'choose';
  

    return (
        <div>
            <div className="form-group  ">
                <div class="input-group-prepend">
                    <label class="input-group-text" for="inputGroupSelect01">نوع السؤال</label>
                </div>
                <select class="custom-select" id="inputGroupSelect01 "  value={selectedExam} onChange={handleExamChange}>

                    <option value="">نوع الامتحان</option>
                    {examOptions.map((option) => (
                        <option key={option.value} value={option.value}>
                            {option.label}
                        </option>
                    ))}
                </select>
            
                {selectedExam && (
                <div>
                    <h3>الاجابة :</h3>
                    {answers.map((answer, index) => (
                        <input
                            className="form-control"
                            key={index}
                            value={answer}
                            onChange={(event) => handleAnswerChange(index, event)}
                        />
                    ))}
                    <button class="btn bg-gradient-success w-100 mb-0 toast-btn mr-2" type="submit" onClick={addAnswer} disabled={isAddAnswerDisabled}>اضافة</button>
                </div>
            )}
               {answers.length > 0 && (
        <div>
          <h3>الإجابات المقدمة:</h3>
          <ul>
            {answers.map((answer, index) => (
              <li key={index}>{answer}</li>
            ))}
          </ul>
        </div>
      )}
         
            </div>
      
        </div>
    );
};

export default ExamForm;
