import React, { useState, useEffect } from 'react';

const SoundInputField = () => {
  const [audioContext, setAudioContext] = useState(null);
  const [mediaStream, setMediaStream] = useState(null);
  const [audioRecorder, setAudioRecorder] = useState(null);
  const [chunks, setChunks] = useState([]);

  useEffect(() => {
    const initializeAudio = async () => {
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const mediaStream = await navigator.mediaDevices.getUserMedia({ audio: true });

      setAudioContext(audioContext);
      setMediaStream(mediaStream);
    };

    initializeAudio();

    return () => {
      if (mediaStream) {
        mediaStream.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const handleStart = () => {
    if (audioContext && mediaStream) {
      const audioSource = audioContext.createMediaStreamSource(mediaStream);
      const audioRecorder = new MediaRecorder(audioSource);

      audioRecorder.addEventListener('dataavailable', (event) => {
        setChunks((prevChunks) => [...prevChunks, event.data]);
      });

      audioRecorder.start();

      setAudioRecorder(audioRecorder);
    }
  };

  const handleStop = () => {
    if (audioRecorder) {
      audioRecorder.stop();
    }
  };

  const handlePlay = () => {
    if (chunks.length > 0) {
      const audioData = new Blob(chunks, { type: 'audio/webm' });
      const audioUrl = URL.createObjectURL(audioData);
      const audioElement = new Audio(audioUrl);

      audioElement.play();
    }
  };

  return (
    <div className="container">
      <h2>Real-time Sound Input Field</h2>
      <div className="mt-3">
        <button className="btn btn-primary mr-2" onClick={handleStart}>
          Start
        </button>
        <button className="btn btn-secondary mr-2" onClick={handleStop}>
          Stop
        </button>
        <button className="btn btn-success" onClick={handlePlay}>
          Play
        </button>
      </div>
    </div>
  );
};

export default SoundInputField;
