import React, { useState } from 'react';

const ExamForm = () => {
  const [selectedExam, setSelectedExam] = useState('');
  const [answers, setAnswers] = useState([]);
  const [correctAnswerIndex, setCorrectAnswerIndex] = useState(-1);

  const examOptions = [
    { value: 'math', label: 'Mathematics' },
    { value: 'science', label: 'Science' },
    { value: 'history', label: 'History' },
    // Add more options as needed
  ];

  const handleExamChange = (event) => {
    const selectedExam = event.target.value;
    setSelectedExam(selectedExam);
    setAnswers([]);
    setCorrectAnswerIndex(-1);
  };

  const handleAnswerChange = (index, event) => {
    const newAnswers = [...answers];
    newAnswers[index] = event.target.value;
    setAnswers(newAnswers);
  };

  const handleCorrectAnswerChange = (index) => {
    setCorrectAnswerIndex(index);
  };

  const addAnswer = () => {
    const newAnswers = [...answers, ''];
    setAnswers(newAnswers);
    setCorrectAnswerIndex(newAnswers.length - 1);
  };

  return (
    <div>
      <select value={selectedExam} onChange={handleExamChange}>
        <option value="">Select an exam type</option>
        {examOptions.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>

      {selectedExam && (
        <div>
          <h3>Answers:</h3>
          {answers.map((answer, index) => (
            <div key={index}>
              <input
                type="text"
                value={answer}
                onChange={(event) => handleAnswerChange(index, event)}
              />
              <label>
                <input
                  type="radio"
                  name="correctAnswer"
                  checked={correctAnswerIndex === index}
                  onChange={() => handleCorrectAnswerChange(index)}
                />{' '}
                Correct Answer
              </label>
            </div>
          ))}
          <button onClick={addAnswer} type="button">Add Answer</button>
        </div>
      )}

      {answers.length > 0 && (
        <div>
          <h3>Submitted Answers:</h3>
          <ul>
            {answers.map((answer, index) => (
              <li key={index}>
                {answer} {correctAnswerIndex === index && '(Correct)'}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ExamForm;
